package Lec5;

public class Char_Demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'a';
		System.out.println(ch);
		System.out.println((int) (ch));
		ch++;// ch = (char)(ch + 1);
		System.out.println(ch);
		ch += 1;// ch = (char)(ch + 1);
		System.out.println(ch);
		//ch = ch + 1;
	}

}
